SELECT AVG("district"."A15") as agg_0, MAX("district"."A5") as agg_1, SUM("district"."A11" + "district"."district_id") as agg_2 FROM "client" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id"  WHERE "client"."client_id" <= 131043 AND "client"."birth_date" IN ('1971-02-28', '1920-01-17', '1967-07-19', '1952-08-26', '1970-10-07', '1970-07-07', '1959-09-14', '1965-07-25') AND "district"."A3" IN ('south Moravia', 'south Bohemia', 'Prague', 'north Moravia', 'west Bohemia', 'east Bohemia') AND "district"."A9" BETWEEN 4 AND 6