SELECT MAX("district"."A13") as agg_0, AVG("district"."A9") as agg_1, SUM("district"."A6" + "district"."A12") as agg_2 FROM "district"  WHERE "district"."A2" IN ('Brno - venkov', 'Svitavy', 'Breclav') AND ("district"."A12" >= 3.1891200610374213 OR ("district"."A12" <= 6.822745678431486 AND "district"."A12" >= 3.351554085815945)) AND "district"."A3" IN ('north Moravia', 'north Bohemia', 'south Bohemia', 'central Bohemia', 'east Bohemia', 'Prague', 'south Moravia')