SELECT MAX("district"."A16" + "trans"."balance") as agg_0, SUM("trans"."account") as agg_1 FROM "account" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id"  WHERE "account"."district_id" BETWEEN 33 AND 316 AND "account"."date" IN ('1996-07-08', '1993-02-08', '1993-10-08', '1993-06-08', '1993-02-27', '1997-08-08', '1996-06-25', '1996-11-24', '1993-11-01') AND "client"."district_id" <= 54 AND "disp"."type" IN ('DISPONENT')