SELECT COUNT(*) as agg_0, AVG("disp"."account_id") as agg_1, MAX("district"."A7" + "district"."A10") as agg_2 FROM "client" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id"  WHERE "client"."district_id" <= 94 AND "district"."A5" = 30 AND ("disp"."client_id" BETWEEN 76667 AND 103734 OR "disp"."type" IN ('DISPONENT'))