SELECT MIN("district"."A7" + "district"."A4") as agg_0 FROM "client" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id"  WHERE "card"."type" NOT LIKE '%l%d%' AND "district"."A14" >= 120 AND "district"."A2" LIKE '%Semily%'