SELECT MIN("client"."client_id") as agg_0, COUNT(*) as agg_1 FROM "district" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE ("district"."A10" BETWEEN 47.060076309408025 AND 57.83899267964936 OR "district"."A3" IN ('central Bohemia', 'south Bohemia', 'south Moravia', 'Prague', 'west Bohemia', 'north Moravia')) AND ("trans"."bank" LIKE '%Y%Z%' OR ("trans"."operation" IS NOT NULL AND "trans"."bank" LIKE '%M%N%')) AND "client"."gender" != 'F' AND "account"."frequency" IN ('POPLATEK PO OBRATU', 'POPLATEK TYDNE') AND "district"."A2" IN ('Brno - venkov', 'Hl.m. Praha', 'Blansko', 'Semily', 'Brno - mesto', 'Breclav', 'Usti nad Orlici', 'Svitavy')