SELECT MAX("district"."A15" + "card"."card_id") as agg_0, COUNT(*) as agg_1, SUM("card"."disp_id") as agg_2 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id"  WHERE "card"."type" NOT LIKE '%cl%assic%' AND "disp"."account_id" >= 15069 AND "card"."card_id" BETWEEN 104 AND 774 AND "disp"."type" IN ('OWNER') AND "disp"."disp_id" BETWEEN 42310 AND 129909 AND "client"."gender" = 'M'