SELECT MIN("card"."disp_id" + "account"."district_id") as agg_0, MAX("card"."card_id" + "account"."account_id") as agg_1, MAX("disp"."client_id" + "card"."disp_id") as agg_2 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id"  WHERE "account"."district_id" >= 66 AND "card"."type" = 'junior' AND ("card"."card_id" <= 417 OR "card"."disp_id" <= 118850 OR "card"."card_id" >= 828) AND ("disp"."type" != 'OWNER' OR "disp"."type" NOT LIKE '%DISPONENT%') AND ("disp"."client_id" <= 12756 OR "disp"."client_id" BETWEEN 45963 AND 75097 OR "disp"."client_id" BETWEEN 14367 AND 126049) AND "account"."frequency" IN ('POPLATEK PO OBRATU')