SELECT MIN("client"."client_id") as agg_0, SUM("disp"."disp_id" + "client"."district_id") as agg_1, MIN("card"."card_id" + "disp"."disp_id") as agg_2 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id"  WHERE ("client"."client_id" <= 105817 OR ("client"."client_id" BETWEEN 71556 AND 83115 AND "client"."client_id" BETWEEN 59127 AND 131104)) AND ("disp"."type" LIKE '%DISPON%ENT%' OR "disp"."type" LIKE '%OWNE%R%' OR "disp"."type" IN ('OWNER'))