SELECT SUM("disp"."account_id" + "disp"."client_id") as agg_0 FROM "district" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id"  WHERE "account"."frequency" LIKE '%MESI%CNE%' AND "district"."A3" != 'north Moravia' AND ("account"."district_id" >= 265 OR "account"."district_id" <= 328) AND "disp"."type" IN ('DISPONENT') AND "card"."type" LIKE '%cla%ssic%'