SELECT AVG("district"."A13") as agg_0, MAX("district"."A4") as agg_1, MIN("district"."A15" + "trans"."account") as agg_2 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "account"."frequency" = 'POPLATEK TYDNE' AND "district"."A2" LIKE '%Pribram%' AND "trans"."trans_id" BETWEEN 160774 AND 1007145 AND ("trans"."k_symbol" NOT LIKE '%POJI%STNE%' OR ("trans"."operation" LIKE '%UCET%' AND "trans"."k_symbol" NOT LIKE '%POJISTN%E%'))