SELECT MIN("trans"."amount" + "disp"."client_id") as agg_0 FROM "account" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id"  WHERE "account"."district_id" <= 316 AND "account"."account_id" BETWEEN 86571 AND 99973 AND "account"."frequency" != 'POPLATEK TYDNE' AND "trans"."operation" NOT LIKE '%UC%ET%' AND "trans"."trans_id" >= 986336 AND "trans"."type" LIKE '%VYBER%'