SELECT SUM("trans"."account") as agg_0 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id"  WHERE "loan"."status" = 'C' AND "account"."frequency" LIKE '%OBRATU%'