SELECT AVG("district"."district_id" + "trans"."account") as agg_0, MIN("disp"."client_id" + "account"."district_id") as agg_1, MIN("district"."A4" + "district"."A7") as agg_2 FROM "client" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "trans"."k_symbol" NOT LIKE '%POJISTN%E%' AND "district"."A3" NOT LIKE '%sou%th%' AND "district"."A9" = 10 AND "disp"."account_id" BETWEEN 1560 AND 14656 AND "district"."A2" NOT LIKE '%Vyskov%' AND ("trans"."operation" != 'VYBER' OR "trans"."account" <= 52008874.21744646)