SELECT AVG("disp"."client_id" + "card"."card_id") as agg_0, AVG("disp"."client_id") as agg_1 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id"  WHERE "disp"."disp_id" <= 125093 AND ("card"."card_id" BETWEEN 672 AND 1160 OR ("card"."issued" IN ('1998-10-11', '1998-12-08', '1998-09-29') AND "card"."card_id" BETWEEN 20 AND 458)) AND "card"."type" NOT LIKE '%c%lassic%' AND "card"."type" != 'ld'