SELECT SUM("loan"."account_id") as agg_0, AVG("trans"."balance" + "trans"."trans_id") as agg_1, COUNT(*) as agg_2 FROM "loan" LEFT OUTER JOIN "account" ON "loan"."account_id" = "account"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "account"."frequency" LIKE '%POPLATEK%' AND "loan"."payments" BETWEEN 3093.5565605047427 AND 3609.967883907081 AND ("trans"."amount" <= 3785 OR "trans"."amount" >= 313) AND "trans"."operation" LIKE '%UCET%' AND "trans"."k_symbol" != 'SIPO'