SELECT COUNT(*) as agg_0, MIN("account"."account_id") as agg_1, MAX("account"."district_id") as agg_2 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id"  WHERE "loan"."status" IN ('D', 'B') AND "trans"."type" NOT LIKE '%PR%IJEM%' AND ("trans"."k_symbol" LIKE '%POJISTNE%' OR ("trans"."date" != '1994-03-22' AND "trans"."k_symbol" = 'DUCHOD')) AND "account"."date" IN ('1996-07-08', '1993-02-08', '1993-11-01', '1996-11-24', '1997-08-08') AND "trans"."k_symbol" != 'POJISTNE' AND "loan"."amount" >= 313957