SELECT AVG("client"."client_id" + "district"."A16") as agg_0, AVG("district"."A6" + "district"."district_id") as agg_1, SUM("client"."client_id" + "district"."A6") as agg_2 FROM "disp" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id"  WHERE "card"."type" NOT LIKE '%classic%' AND "disp"."type" NOT LIKE '%DISPONENT%' AND "card"."type" != 'junior'