SELECT AVG("account"."district_id") as agg_0, MAX("account"."district_id") as agg_1 FROM "account"  WHERE "account"."account_id" <= 37662 AND "account"."frequency" IN ('POPLATEK PO OBRATU', 'POPLATEK MESICNE') AND ("account"."district_id" BETWEEN 57 AND 450 OR ("account"."district_id" <= 38 AND "account"."district_id" BETWEEN 430 AND 560)) AND "account"."date" IN ('1993-06-08', '1996-07-08', '1996-07-21', '1996-11-24')