SELECT MAX("card"."disp_id") as agg_0, MIN("card"."card_id" + "card"."disp_id") as agg_1, SUM("card"."card_id") as agg_2 FROM "card"  WHERE "card"."type" LIKE '%junio%r%' AND ("card"."card_id" BETWEEN 662 AND 887 OR "card"."issued" != '1997-08-01' OR "card"."card_id" <= 630) AND ("card"."type" != 'classic' OR ("card"."issued" IN ('1997-05-17', '1998-10-11', '1998-12-08', '1998-02-26', '1998-12-23', '1998-09-29', '1998-07-07') AND "card"."type" = 'ld'))