SELECT AVG("disp"."disp_id") as agg_0, SUM("disp"."client_id") as agg_1 FROM "disp" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "disp"."disp_id" >= 79619 AND "disp"."type" IN ('OWNER') AND ("disp"."client_id" <= 60662 OR "disp"."account_id" <= 18525 OR "disp"."client_id" <= 59302)