SELECT MIN("district"."A7") as agg_0, AVG("district"."A9") as agg_1 FROM "disp" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id"  WHERE "disp"."type" = 'OWNER' AND "district"."A13" BETWEEN 2.7465548375964026 AND 3.2592422150594382 AND "card"."disp_id" >= 40216 AND "account"."date" != '1996-07-08' AND "client"."district_id" BETWEEN 20 AND 703