SELECT SUM("account"."district_id") as agg_0, MIN("district"."A11" + "district"."A14") as agg_1 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "district"."A2" IN ('Usti nad Orlici', 'Brno - mesto', 'Svitavy', 'Blansko', 'Breclav', 'Hodonin', 'Semily', 'Trutnov', 'Hl.m. Praha') AND "account"."frequency" = 'POPLATEK PO OBRATU' AND "district"."A3" IN ('central Bohemia') AND "trans"."operation" = 'VYBER'