SELECT AVG("loan"."duration" + "trans"."trans_id") as agg_0 FROM "account" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id"  WHERE "trans"."operation" NOT LIKE '%PREVOD%' AND ("account"."district_id" BETWEEN 54 AND 428 OR ("account"."frequency" IN ('POPLATEK PO OBRATU') AND "account"."district_id" <= 423)) AND ("trans"."bank" NOT LIKE '%GH%' OR "trans"."trans_id" BETWEEN 1284728 AND 1808712) AND "trans"."type" NOT LIKE '%VYDAJ%' AND "account"."account_id" <= 61149