SELECT MIN("client"."district_id" + "disp"."account_id") as agg_0, AVG("account"."district_id" + "client"."district_id") as agg_1 FROM "account" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id"  WHERE "client"."client_id" >= 35123 AND "account"."district_id" BETWEEN 8 AND 534 AND ("disp"."disp_id" <= 75526 OR ("disp"."disp_id" >= 79082 AND "disp"."disp_id" BETWEEN 57702 AND 124953))