SELECT MAX("trans"."account") as agg_0, MIN("disp"."disp_id" + "card"."card_id") as agg_1, SUM("trans"."balance" + "card"."disp_id") as agg_2 FROM "disp" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "account"."frequency" IN ('POPLATEK TYDNE', 'POPLATEK PO OBRATU') AND "card"."issued" = '1996-10-06' AND ("disp"."type" IN ('DISPONENT') OR "disp"."account_id" >= 7904) AND ("account"."account_id" >= 92181 OR "account"."district_id" >= 471)