SELECT MIN("district"."A13") as agg_0, SUM("loan"."loan_id" + "district"."A4") as agg_1 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id"  WHERE "district"."A2" NOT LIKE '%Mo%st%' AND "trans"."amount" >= 9639