SELECT COUNT(*) as agg_0 FROM "district" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id"  WHERE "district"."A9" != 7 AND "account"."account_id" >= 88140 AND "account"."frequency" NOT LIKE '%T%YDNE%' AND "trans"."bank" NOT LIKE '%QR%'