SELECT SUM("district"."A9" + "district"."A5") as agg_0, AVG("district"."district_id" + "district"."A10") as agg_1 FROM "district" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "district"."A8" = 1 AND ("card"."disp_id" BETWEEN 87802 AND 100049 OR "card"."type" NOT LIKE '%c%lassic%')