SELECT COUNT(*) as agg_0, SUM("account"."account_id") as agg_1, SUM("card"."disp_id" + "loan"."payments") as agg_2 FROM "loan" LEFT OUTER JOIN "account" ON "loan"."account_id" = "account"."account_id" LEFT OUTER JOIN "disp" ON "account"."account_id" = "disp"."account_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "account"."frequency" NOT LIKE '%OBR%ATU%' AND "disp"."type" IN ('DISPONENT') AND "account"."account_id" BETWEEN 46606 AND 111841 AND "card"."issued" IN ('1998-09-29', '1998-07-07', '1998-04-16', '1998-12-23', '1998-02-26', '1998-10-11') AND ("account"."frequency" IN ('POPLATEK PO OBRATU') OR ("account"."date" != '1993-05-11' AND "account"."frequency" LIKE '%POPL%ATEK%'))