SELECT MIN("district"."A7") as agg_0, SUM("district"."A16" + "district"."A8") as agg_1 FROM "client" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "disp" ON "client"."client_id" = "disp"."client_id" LEFT OUTER JOIN "card" ON "disp"."disp_id" = "card"."disp_id"  WHERE "district"."A4" != 110940 AND "district"."A16" != 3652 AND "disp"."account_id" >= 684 AND "card"."card_id" >= 258