SELECT MAX("trans"."amount" + "trans"."trans_id") as agg_0, AVG("trans"."account_id") as agg_1 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id"  WHERE "trans"."balance" BETWEEN 48637 AND 61890 AND "trans"."operation" LIKE '%UCTU%' AND "account"."frequency" != 'POPLATEK TYDNE' AND "trans"."k_symbol" IN ('SLUZBY', 'SIPO', 'UVER', ' ', 'POJISTNE')