SELECT MIN("client"."client_id" + "account"."account_id") as agg_0 FROM "disp" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id"  WHERE "disp"."type" LIKE '%OWNER%' AND "account"."date" != '1996-10-24' AND ("account"."district_id" <= 306 OR ("account"."district_id" >= 370 AND "account"."district_id" <= 457))