SELECT MAX("district"."A14" + "district"."A15") as agg_0, SUM("trans"."amount" + "district"."A12") as agg_1, MIN("trans"."account") as agg_2 FROM "trans" LEFT OUTER JOIN "account" ON "trans"."account_id" = "account"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "client" ON "district"."district_id" = "client"."district_id"  WHERE "account"."frequency" = 'POPLATEK MESICNE'