SELECT SUM("card"."disp_id" + "client"."district_id") as agg_0, MIN("account"."account_id" + "client"."district_id") as agg_1, AVG("card"."disp_id") as agg_2 FROM "card" LEFT OUTER JOIN "disp" ON "card"."disp_id" = "disp"."disp_id" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "account" ON "disp"."account_id" = "account"."account_id"  WHERE "account"."account_id" <= 73256 AND ("account"."frequency" LIKE '%OBRATU%' OR ("account"."date" = '1994-05-19' AND "account"."frequency" IN ('POPLATEK TYDNE'))) AND "card"."disp_id" BETWEEN 21629 AND 84872