SELECT SUM("district"."A8") as agg_0, AVG("district"."A8" + "district"."A11") as agg_1 FROM "account" LEFT OUTER JOIN "trans" ON "account"."account_id" = "trans"."account_id" LEFT OUTER JOIN "district" ON "account"."district_id" = "district"."district_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id"  WHERE "account"."frequency" = 'POPLATEK PO OBRATU' AND "account"."frequency" IN ('POPLATEK TYDNE')