SELECT MIN("district"."A11" + "loan"."amount") as agg_0, MIN("district"."A6") as agg_1, MAX("account"."district_id" + "district"."A6") as agg_2 FROM "disp" LEFT OUTER JOIN "client" ON "disp"."client_id" = "client"."client_id" LEFT OUTER JOIN "district" ON "client"."district_id" = "district"."district_id" LEFT OUTER JOIN "account" ON "district"."district_id" = "account"."district_id" LEFT OUTER JOIN "loan" ON "account"."account_id" = "loan"."account_id"  WHERE "account"."frequency" LIKE '%T%YDNE%' AND "disp"."type" NOT LIKE '%OWNER%' AND "district"."A2" IN ('Brno - mesto', 'Svitavy', 'Trutnov', 'Breclav', 'Hodonin', 'Usti nad Orlici', 'Blansko', 'Semily') AND ("client"."district_id" BETWEEN 533 AND 745 OR "client"."client_id" <= 41430 OR "client"."district_id" BETWEEN 11 AND 46) AND "account"."date" IN ('1993-02-08')