
SELECT MAX("movies"."runningtime") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE ("movies"."year" != 4 OR "movies"."year" BETWEEN 3 AND 4) AND "movies2directors"."genre" LIKE '%Documentary%' AND "directors"."d_quality" <= 3 AND "movies2actors"."movieid" IS NOT NULL;