
SELECT SUM("movies2directors"."directorid") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE ("movies2directors"."genre" NOT LIKE '%Crime%' OR "movies2directors"."genre" IN ('Animation', 'Action', 'Drama', 'Crime', 'Documentary') OR "movies2directors"."genre" != '%Comedy%') AND "movies"."country" != '%UK%' AND "movies2directors"."genre" IN ('Crime', 'Comedy', 'Animation', 'Other', 'Drama', 'Horror', 'Adventure') AND ("movies2directors"."directorid" BETWEEN 150797 AND 277262 OR "movies2directors"."directorid" <= 75710) AND ("movies2actors"."movieid" <= 1756512 OR "movies2actors"."movieid" <= 2274404) AND "movies2directors"."movieid" >= 1842524;