
SELECT MAX("movies2actors"."movieid") as agg_0 FROM "movies2actors" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid"  WHERE "movies"."country" LIKE '%UK%' AND "movies2directors"."genre" NOT LIKE '%Action%' AND "actors"."a_gender" IN ('M') AND "movies2directors"."directorid" >= 147218 AND ("movies2actors"."actorid" <= 189607 OR ("movies2actors"."cast_num" <= 2 AND "movies2actors"."actorid" >= 31902));