
SELECT SUM("u2base"."userid") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "u2base"."movieid" BETWEEN 1961174 AND 2360551 AND "movies2directors"."genre" IN ('Crime', 'Adventure', 'Horror', 'Documentary', 'Drama', 'Comedy', 'Action', 'Animation') AND "users"."u_gender" IN ('F') AND ("movies"."runningtime" >= 3 OR "movies"."isEnglish" IN ('T') OR "movies"."runningtime" <= 3);