
SELECT MIN("directors"."d_quality") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid"  WHERE "movies"."country" IN ('USA', 'UK', 'France') AND "directors"."avg_revenue" <= 0 AND "actors"."a_quality" = 4 AND "movies2directors"."genre" IN ('Crime', 'Drama', 'Adventure', 'Action', 'Animation') AND ("directors"."directorid" <= 283010 OR "directors"."directorid" <= 167569 OR "directors"."directorid" BETWEEN 61989 AND 283347);