
SELECT COUNT(*) as agg_0 FROM "movies2actors" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid"  WHERE "movies2actors"."movieid" <= 2517024 AND ("movies2directors"."directorid" <= 185787 OR ("movies2directors"."directorid" BETWEEN 40912 AND 91260 AND "movies2directors"."directorid" BETWEEN 43663 AND 300483)) AND "movies"."runningtime" BETWEEN 1 AND 2 AND "directors"."directorid" IS NOT NULL;