
SELECT MIN("users"."userid") as agg_0 FROM "u2base" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE "movies2directors"."genre" IN ('Drama', 'Crime', 'Action', 'Other', 'Animation', 'Documentary') AND "movies"."isEnglish" IN ('F') AND "u2base"."rating" != 4 AND ("users"."u_gender" != '%F%' OR "users"."age" != 35 OR "users"."u_gender" IN ('M')) AND "movies2actors"."movieid" IS NOT NULL;