
SELECT MAX("u2base"."rating") as agg_0 FROM "u2base" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "actors" ON "movies2actors"."actorid" = "actors"."actorid"  WHERE "movies2directors"."genre" != '%Action%' AND ("movies2actors"."cast_num" = 3 OR "movies2actors"."actorid" BETWEEN 2327391 AND 2490876) AND "users"."userid" IS NOT NULL AND "actors"."actorid" IS NOT NULL;