
SELECT MIN("movies2directors"."movieid") as agg_0 FROM "actors" LEFT OUTER JOIN "movies2actors" ON "actors"."actorid" = "movies2actors"."actorid" LEFT OUTER JOIN "movies" ON "movies2actors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid"  WHERE "u2base"."rating" = 3 AND ("movies2directors"."genre" LIKE '%Drama%' OR "movies2directors"."movieid" >= 2565732 OR "movies2directors"."genre" LIKE '%Documentary%') AND "actors"."a_gender" != '%M%' AND "movies2directors"."genre" NOT LIKE '%Other%' AND "movies2directors"."directorid" <= 16583 AND "movies2actors"."cast_num" != 3;