
SELECT SUM("movies"."year") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "users"."age" >= 50 AND "directors"."directorid" <= 47476 AND "movies2directors"."genre" IN ('Adventure', 'Comedy', 'Drama') AND "movies"."isEnglish" IN ('T') AND "movies"."runningtime" != 3 AND "movies2actors"."movieid" IS NOT NULL;