
SELECT MIN("movies"."year") as agg_0 FROM "users" LEFT OUTER JOIN "u2base" ON "users"."userid" = "u2base"."userid" LEFT OUTER JOIN "movies" ON "u2base"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2directors" ON "movies"."movieid" = "movies2directors"."movieid"  WHERE "users"."u_gender" IN ('F') AND "users"."occupation" >= 4 AND "movies"."isEnglish" != '%F%' AND "movies2directors"."genre" LIKE '%Documentary%' AND ("movies"."country" IN ('USA') OR "movies"."country" = '%USA%');