
SELECT MIN("directors"."d_quality") as agg_0 FROM "movies2directors" LEFT OUTER JOIN "directors" ON "movies2directors"."directorid" = "directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid" LEFT OUTER JOIN "u2base" ON "movies"."movieid" = "u2base"."movieid" LEFT OUTER JOIN "users" ON "u2base"."userid" = "users"."userid"  WHERE "movies2directors"."genre" NOT LIKE '%Cri%me%' AND "u2base"."rating" >= 3 AND "movies"."movieid" >= 2237704 AND "movies2actors"."movieid" IS NOT NULL AND "users"."userid" IS NOT NULL;