
SELECT AVG("directors"."directorid") as agg_0 FROM "directors" LEFT OUTER JOIN "movies2directors" ON "directors"."directorid" = "movies2directors"."directorid" LEFT OUTER JOIN "movies" ON "movies2directors"."movieid" = "movies"."movieid" LEFT OUTER JOIN "movies2actors" ON "movies"."movieid" = "movies2actors"."movieid"  WHERE "movies"."isEnglish" IN ('F') AND "movies2directors"."genre" != '%Adventure%' AND ("movies2directors"."directorid" <= 1380 OR ("movies2directors"."movieid" BETWEEN 2299407 AND 2403182 AND "movies2directors"."directorid" >= 122430)) AND "movies2actors"."actorid" BETWEEN 1707703 AND 2324058;